import React, { useEffect, useState } from 'react';
import api from './services/api';
import './App.css';

function App() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    api.get('/items')
      .then(response => {
        setProjects(response.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setError('Impossible de charger les projets.');
        setLoading(false);
      });
  }, []);

  if (loading) return <p className="loading">Chargement des projets...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div>
      {/* Header */}
      <header>
        <h1>MyGlowSkills</h1>
        <nav>
          <a href="#projects">Projets</a>
          <a href="#contact">Contact</a>
        </nav>
      </header>

      {/* Main */}
      <main>
        <h2 id="projects">Mes Projets</h2>
        <div className="projects-grid">
          {projects.map(project => (
            <div key={project.id} className="project-card">
              <h3>{project.name}</h3>
              <p>ID: {project.id}</p>
            </div>
          ))}
        </div>
      </main>

      {/* Footer */}
      <footer id="contact">
        <p>© 2025 MyGlowSkills. Tous droits réservés.</p>
        <p>Contact : <a href="mailto:contact@myglowskills.com">contact@myglowskills.com</a></p>
      </footer>
    </div>
  );
}

export default App;
