import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { useNavigate, Link } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // üîπ Redirection automatique si d√©j√† connect√©
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) navigate('/projects');
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null); // reset erreur √† chaque submission
    try {
      const res = await api.post('/auth/login', { 
        username: username.toLowerCase(), // envoie en minuscules
        password 
      });

      // Stockage du token et redirection
      localStorage.setItem('token', res.data.token);
      navigate('/projects');

    } catch (err) {
      // Gestion des erreurs du backend
      if (err.response && err.response.data && err.response.data.message) {
        setError(err.response.data.message);
      } else {
        setError('Erreur lors de la connexion');
      }
    }
  };

  return (
    <div className="auth-form">
      <h2>Connexion</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleLogin}>
        <input 
          type="text" 
          placeholder="Nom d'utilisateur" 
          value={username} 
          onChange={e => setUsername(e.target.value)} 
          required 
        />
        <input 
          type="password" 
          placeholder="Mot de passe" 
          value={password} 
          onChange={e => setPassword(e.target.value)} 
          required 
        />
        <button type="submit">Se connecter</button>
      </form>
      <p>
        Pas encore inscrit ? <Link to="/register">Cr√©er un compte</Link>
      </p>
    </div>
  );
}

export default Login;
